package mc.sn.day2;

public class 산술연산자 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10;
		double b =3;
		
		System.out.println(a+b);
		System.out.println(a-b);
		System.out.println(a*b);
		System.out.println(a/b);
		System.out.println(a%b);
		// %가 제일 많이 헷갈렸다. % 정의는 만약에
		//피연산자와 또다른 피연산자 사이에 % 이게 있으면 좌측 피연산자를 우츨 피연산자로 나눈 나머지를 구하는 연산이다.

	}

}
